var net = require('net');
var Ipconfig = require('Ipconfig');


// Variables and PORT information for the exchange.
HOST = Ipconfig.myIpAddress().toString();
MESSAGINGPORT = 18160;

// This is the server to listen the Messages.

exports.sendMessage = function (SENDIP,Message) {
        var socket = new net.Socket();
        socket.connect(MESSAGINGPORT,SENDIP);
        console.log("Sent Message : From Lappy");
        socket.write(Message);

        socket.on('error', function(ex) {
            console.log("handled error Connecting to : " + SENDIP + ':' + MESSAGINGPORT);
            console.log(ex);
        });
};
