var Ipconfig = require('./Ipconfig');
var dgram = require('dgram');

HOST = Ipconfig.myIpAddress();
VirtualIpArr = HOST.split('.');
VirtualIP = '253';
for (var i = 1; i < VirtualIpArr.length; i++) {
    VirtualIP += ('.' + VirtualIpArr[i]);
}

console.log(VirtualIP);

var client = dgram.createSocket('udp4');
var udpServer = dgram.createSocket('udp4');


udpServer.bind(2119,function(){
    udpServer.addMembership('224.0.0.14',HOST);
});
